import { useAuthContext } from "../hooks/useAuthContext"
const { user } = useAuthContext()

const userDetails = ({ user }) => {
    if(!user) return
    return(
    <div className="user-details">
        <h1>{user.email}</h1>
        <p><strong>Password:</strong>{user.password}</p>

        <p>{user.createdAt}</p>
    </div>)
}

export default userDetails
